export const PMI_GROUPS = ["Planejamento", "Backoffice", "Frontoffice", "Rede e Operações"] as const;

export const DEADLINE_ORDER = ["D15", "D30", "D45", "D60", "D90", "D180+"] as const;

export const STATUS_OPTIONS = [
  { value: "pending", label: "Pendente" },
  { value: "in_progress", label: "Em Andamento" },
  { value: "completed", label: "Concluído" },
  { value: "blocked", label: "Bloqueado" },
] as const;

export function getDeadlineColor(deadline: string): string {
  switch (deadline) {
    case "D15": return "text-red-600 bg-red-50 border-red-200";
    case "D30": return "text-orange-600 bg-orange-50 border-orange-200";
    case "D45": return "text-amber-600 bg-amber-50 border-amber-200";
    case "D60": return "text-yellow-600 bg-yellow-50 border-yellow-200";
    case "D90": return "text-green-600 bg-green-50 border-green-200";
    case "D180+": return "text-blue-600 bg-blue-50 border-blue-200";
    default: return "text-muted-foreground bg-muted";
  }
}

export function getStatusColor(status: string): string {
  switch (status) {
    case "pending": return "text-muted-foreground bg-muted";
    case "in_progress": return "text-blue-700 bg-blue-100";
    case "completed": return "text-green-700 bg-green-100";
    case "blocked": return "text-red-700 bg-red-100";
    default: return "text-muted-foreground bg-muted";
  }
}

export function getStatusLabel(status: string): string {
  const found = STATUS_OPTIONS.find(s => s.value === status);
  return found ? found.label : status;
}
